import { serve } from "https://deno.land/std@0.168.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2";
import { encode as base64Encode } from "https://deno.land/std@0.168.0/encoding/base64.ts";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

// Base64 encoded grumpy cat image - small preview version
const grumpyCatBase64 = "";

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    const supabaseUrl = Deno.env.get("SUPABASE_URL")!;
    const supabaseServiceKey = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY")!;
    
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Try to fetch from deployed app URL
    let catImageBlob: ArrayBuffer;
    try {
      const catImageResponse = await fetch("https://id-preview--512196e1-15b4-4d03-9fef-fb5ab2871b3b.lovable.app/images/grumpy-cat-logo.jpg");
      if (catImageResponse.ok) {
        catImageBlob = await catImageResponse.arrayBuffer();
        console.log("Fetched image from app, size:", catImageBlob.byteLength);
      } else {
        throw new Error("Failed to fetch from app");
      }
    } catch (e) {
      console.log("Falling back to base64 placeholder");
      // Decode base64 as fallback
      const binaryString = atob(grumpyCatBase64);
      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }
      catImageBlob = bytes.buffer;
    }

    // Upload the cat image to storage
    const { data, error } = await supabase.storage
      .from("assets")
      .upload("grumpy-cat-logo.jpg", catImageBlob, {
        contentType: "image/jpeg",
        upsert: true,
      });

    if (error) {
      console.error("Upload error:", error);
      return new Response(
        JSON.stringify({ error: error.message }),
        { status: 500, headers: { ...corsHeaders, "Content-Type": "application/json" } }
      );
    }

    // Get the public URL
    const { data: urlData } = supabase.storage
      .from("assets")
      .getPublicUrl("grumpy-cat-logo.jpg");

    console.log("Cat logo uploaded successfully:", urlData.publicUrl);

    return new Response(
      JSON.stringify({ url: urlData.publicUrl }),
      { headers: { ...corsHeaders, "Content-Type": "application/json" } }
    );
  } catch (error) {
    console.error("Error:", error);
    return new Response(
      JSON.stringify({ error: error instanceof Error ? error.message : "Unknown error" }),
      { status: 500, headers: { ...corsHeaders, "Content-Type": "application/json" } }
    );
  }
});
